import React, { useState, useRef, useEffect } from "react";
import chillHop from "../data";
import "./Player.css";

const Player = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [songProgress, setSongProgress] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const audioRef = useRef(null);
  const songs = chillHop;
  const [remainingTime, setRemainingTime] = useState(0);

  const onTimeUpdate = () => {
    if (audioRef.current) {
      const currentTime = audioRef.current.currentTime;
      const duration = audioRef.current.duration;
      setCurrentTime(currentTime);
      setRemainingTime(duration - currentTime);
      setSongProgress((currentTime / duration) * 100);
    }
  };

  const onLoadedMetadata = () => {
    if (audioRef.current) {
      setDuration(audioRef.current.duration);
    }
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const playNextSong = () => {
    setCurrentSongIndex((prevIndex) => (prevIndex + 1) % songs.length);
  };

  const playPrevSong = () => {
    setCurrentSongIndex(
      (prevIndex) => (prevIndex - 1 + songs.length) % songs.length
    );
  };

  useEffect(() => {
    if (isPlaying && audioRef.current) {
      audioRef.current.play();
    } else if (!isPlaying && audioRef.current) {
      audioRef.current.pause();
    }
  }, [isPlaying, currentSongIndex]);

  useEffect(() => {
    setRemainingTime(duration - currentTime);
  }, [currentTime, duration]);

  function formatTime(seconds) {
    return `${Math.floor(seconds / 60)}:${`0${Math.floor(seconds % 60)}`.slice(
      -2
    )}`;
  }

  return (
    <div className="player">
      <div className="current-song">
        <img
          src={songs[currentSongIndex].cover}
          alt={songs[currentSongIndex].name}
          className="current-song-cover"
        />
        <h2 className="current-song-name">{songs[currentSongIndex].name}</h2>
        <h3 className="current-song-artist">
          {songs[currentSongIndex].artist}
        </h3>
      </div>
      <div className="player-controls">
        <button onClick={playPrevSong} className="control-button">
          <i className="fas fa-step-backward"></i>
        </button>
        <button onClick={togglePlayPause} className="control-button">
          {isPlaying ? (
            <i className="fas fa-pause"></i>
          ) : (
            <i className="fas fa-play"></i>
          )}
        </button>
        <button onClick={playNextSong} className="control-button">
          <i className="fas fa-step-forward"></i>
        </button>
      </div>
      <div className="progress-container">
        <div className="progress-bar">
          <div className="progress" style={{ width: `${songProgress}%` }}></div>
        </div>
        <div className="progress-time">
          <span>{formatTime(currentTime)}</span>
          <span>-{formatTime(remainingTime)}</span>
        </div>
      </div>
      <audio
        ref={audioRef}
        src={songs[currentSongIndex].audio}
        onLoadedMetadata={onLoadedMetadata}
        onTimeUpdate={onTimeUpdate}
        onEnded={playNextSong}
      ></audio>
    </div>
  );
};

export default Player;
