import "./Player.css";
import React, { useState, useRef, useEffect } from "react";
import chillHop from "../data"; // Adjust the import based on your data file's location

const Player = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [songProgress, setSongProgress] = useState(0);
  const audioRef = useRef(null);
  const songs = chillHop();

  // Handles updating the song progress
  const onTimeUpdate = () => {
    const progress =
      (audioRef.current.currentTime / audioRef.current.duration) * 100;
    setSongProgress(progress);
  };

  // Toggles play/pause
  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
    if (!isPlaying) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
    }
  };

  // Plays the next song
  const playNextSong = () => {
    setCurrentSongIndex((prevIndex) => (prevIndex + 1) % songs.length);
  };

  // Plays the previous song
  const playPreviousSong = () => {
    setCurrentSongIndex(
      (prevIndex) => (prevIndex - 1 + songs.length) % songs.length
    );
  };

  // Effect for auto-play when the song changes
  useEffect(() => {
    if (isPlaying) {
      audioRef.current.play();
    }
  }, [currentSongIndex, isPlaying]);

  // JSX for the player component
  return (
    <div className="player">
      <h3>
        {songs[currentSongIndex].name} - {songs[currentSongIndex].artist}
      </h3>
      <div className="controls">
        <button onClick={playPreviousSong}>⏮️</button>{" "}
        {/* Replace with icons */}
        <button onClick={togglePlayPause}>
          {isPlaying ? "⏸️" : "▶️"}
        </button>{" "}
        {/* Replace with icons */}
        <button onClick={playNextSong}>⏭️</button> {/* Replace with icons */}
      </div>
      {/* Progress Bar */}
      <div className="progress-container">
        <div className="progress-bar">
          <div
            className="progress"
            style={{ width: `${songProgress}%` }} // This updates based on songProgress state
          ></div>
        </div>
        <div className="progress-time">
          {/* Current time and total duration here */}
        </div>
      </div>

      {/* Audio Element */}
      <audio
        ref={audioRef}
        // ... (other attributes)
      ></audio>
    </div>
  );
};

export default Player;
