import React, { useState, useRef, useEffect } from "react";
import chillHop from "../data";
import "./Player.css";

const Player = ({
  currentSong,
  setCurrentSongIndex,
  currentSongIndex,
  songs,
}) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [songProgress, setSongProgress] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const audioRef = useRef(null);
  const [remainingTime, setRemainingTime] = useState(0);

  const onTimeUpdate = () => {
    if (audioRef.current) {
      const currentTime = audioRef.current.currentTime;
      if (!isNaN(currentTime)) {
        setCurrentTime(currentTime);
        setRemainingTime(duration - currentTime);
        setSongProgress((currentTime / duration) * 100);
      }
    }
  };

  const onLoadedMetadata = () => {
    if (audioRef.current && !isNaN(audioRef.current.duration)) {
      setDuration(audioRef.current.duration);
    }
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const setSeekBar = (e) => {
    const width = e.target.clientWidth;
    const clickX = e.nativeEvent.offsetX;
    const duration = audioRef.current.duration;
    audioRef.current.currentTime = (clickX / width) * duration;
  };

  const playNextSong = () => {
    setCurrentSongIndex((prevIndex) => (prevIndex + 1) % songs.length);
    resetPlayback();
  };

  const playPrevSong = () => {
    setCurrentSongIndex((prevIndex) => {
      return prevIndex === 0 ? songs.length - 1 : prevIndex - 1;
    });
    resetPlayback();
  };

  const resetPlayback = () => {
    setCurrentTime(0);
    setSongProgress(0);
    setRemainingTime(duration);
    if (audioRef.current) {
      audioRef.current.currentTime = 0;
    }
  };

  useEffect(() => {
    if (isPlaying && audioRef.current) {
      audioRef.current.play();
    } else if (audioRef.current) {
      audioRef.current.pause();
    }
  }, [isPlaying]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.src = currentSong.audio;
      audioRef.current.load();
      resetPlayback(); // Reset the playback whenever the song changes
      if (isPlaying) {
        setTimeout(() => audioRef.current.play(), 100); // A slight delay to ensure the new song loads
      }
    }
  }, [currentSong]);

  function formatTime(seconds) {
    if (isNaN(seconds)) {
      return "0:00";
    }
    return `${Math.floor(seconds / 60)}:${`0${Math.floor(seconds % 60)}`.slice(
      -2
    )}`;
  }

  return (
    <div className="player">
      {/* Player UI components */}
      <audio
        ref={audioRef}
        src={currentSong.audio}
        onLoadedMetadata={onLoadedMetadata}
        onTimeUpdate={onTimeUpdate}
        onEnded={playNextSong}
      ></audio>
    </div>
  );
};

export default Player;
