import React, { useState, useRef, useEffect } from "react";
import songs from "./data"; // Adjust this import based on your data file's name and location

const Player = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [songProgress, setSongProgress] = useState(0);
  const audioRef = useRef(null);

  // Update song progress
  const onTimeUpdate = () => {
    if (audioRef.current) {
      const progress =
        (audioRef.current.currentTime / audioRef.current.duration) * 100;
      setSongProgress(progress);
    }
  };

  // Play/Pause toggle
  const togglePlayPause = () => {
    const prevValue = isPlaying;
    setIsPlaying(!prevValue);
    if (!prevValue) {
      audioRef.current.play();
    } else {
      audioRef.current.pause();
    }
  };

  // Handle next song
  const playNextSong = () => {
    let newIndex = currentSongIndex + 1;
    if (newIndex >= songs.length) {
      newIndex = 0;
    }
    setCurrentSongIndex(newIndex);
  };

  // Handle previous song
  const playPreviousSong = () => {
    let newIndex = currentSongIndex - 1;
    if (newIndex < 0) {
      newIndex = songs.length - 1;
    }
    setCurrentSongIndex(newIndex);
  };

  // Update the playing song when currentSongIndex changes
  useEffect(() => {
    if (isPlaying) {
      audioRef.current.play();
    }
  }, [currentSongIndex, isPlaying]);

  return (
    <div>
      <h3>
        {songs[currentSongIndex].name} - {songs[currentSongIndex].artist}
      </h3>
      <button onClick={togglePlayPause}>{isPlaying ? "Pause" : "Play"}</button>
      <button onClick={playPreviousSong}>Previous</button>
      <button onClick={playNextSong}>Next</button>
      <audio
        ref={audioRef}
        src={songs[currentSongIndex].audio}
        onTimeUpdate={onTimeUpdate}
        onEnded={playNextSong} // Automatically play next song when the current one ends
      ></audio>
      <div>Progress: {songProgress.toFixed(2)}%</div>
    </div>
  );
};

export default Player;
